client_script:
frappe.web_form.after_load = () => {
	get_contact_name();
	get_total_objects();
}

$(document).ready(function(){
	frappe.web_form.after_load();
});

function get_contact_name() {
    var myurl = '/api/resource/Contact?filters=[["user","=","' + frappe.session.user + '"]]';
	$.ajax({
		type: 'GET', 
		url: myurl,
		success: function(result) {
			var options = [];
			for (var i = 0; i < result.data.length; i++) {
				options.push({
					'label': result.data[i].name,
					'value': result.data[i].name
				});
			}

			var field = frappe.web_form.get_field("contact");
			field._data = options;
			field.refresh();
			$('[data-fieldname="contact"]').val(options[0].value);
		}
	});
};

function get_total_objects() {
    var myurl = '/api/resource/Gesamtobjekt';
	$.ajax({
		type: 'GET', 
		url: myurl,
		success: function(result) {
			var options = [];
			for (var i = 0; i < result.data.length; i++) {
				options.push({
					'label': result.data[i].name,
					'value': result.data[i].name
				});
			}

			var field = frappe.web_form.get_field("total_object");
			field._data = options;
			field.refresh();
		}
	});
};


frappe.web_form.on('total_object', (field, value) => {
    if (value) {
       filterMietobjekt(value);
    }
});

function filterMietobjekt(object_name) {
    var myurl = '/api/resource/Mietobjekt?filters=[["total_object","=","' + object_name + '"]]';
	$.ajax({
		type: 'GET', 
		url: myurl,
		success: function(result) {
			var options = [];
			for (var i = 0; i < result.data.length; i++) {
				options.push({
					'label': result.data[i].name,
					'value': result.data[i].name
				});
			}
			var field = frappe.web_form.get_field("object_name");
			field._data = options;
			field.refresh();
		}
	});
};
